import { TextField, Button, Grid2, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Typography, FormControlLabel, Checkbox } from "@mui/material";
import { useState } from "react";

// Define the backend URL
const API_URL = "https://cuddly-adventure-g4xwpjpw4v7r25vv-5000.app.github.dev//api/tasks"; // Replace with the actual backend URL if different

const CreateTask = () => {
  const [formData, setFormData] = useState({
    taskid: "",
    task: "",
    subject: "",
    desc: "",
    startdate: "",
    deadline: "",
    completed: false,
    percentComp: 0
  });

  const [tasks, setTasks] = useState([
    {
      taskid: 1,
      task: "History Essay",
      subject: "History",
      desc: "World War II analysis",
      startdate: "2025-03-05",
      deadline: "2025-03-10",
      completed: false,
      percentComp: 0
    },
    {
      taskid: 2,
      task: "Math Homework",
      subject: "Math",
      desc: "Algebra exercises",
      startdate: "2025-03-01",
      deadline: "2025-03-07",
      completed: false,
      percentComp: 0
    }
  ]);

  const handleClear = () => {
    setFormData({
      taskid: "",
      task: "",
      subject: "",
      desc: "",
      startdate: "",
      deadline: "",
      completed: false,
      percentComp: 0
    });
  };

  const handleTextChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleCheckboxChange = () => {
    setFormData((prev) => ({
      ...prev,
      completed: !prev.completed,
    }));
  };

  const handleSubmit = async () => {
    if (formData.taskid) {
      // Update existing task logic (optional)
      setTasks((prevTasks) =>
        prevTasks.map((task) =>
          task.taskid === formData.taskid ? formData : task
        )
      );
    } else {
      // Create a new task and send to backend
      try {
        const response = await fetch(API_URL, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(formData),
        });
        const data = await response.json();
        if (response.ok) {
          setTasks((prev) => [...prev, data.task]); // Add task from response
          handleClear();
        } else {
          console.error("Error adding task:", data.message);
        }
      } catch (error) {
        console.error("Error submitting task:", error);
      }
    }
  };

  const handleDelete = (taskid) => {
    setTasks((prev) => prev.filter((task) => task.taskid !== taskid));
  };

  const handleEdit = (task) => {
    setFormData(task);
  };

  return (
    <div style={{ padding: "20px" }}>
      <Grid2 container spacing={5} direction="row" justifyContent="center" alignItems="center">
        {/* Form Fields */}
        {/* Your existing code for input fields */}
      </Grid2>

      {/* Task List Table */}
      <Paper sx={{ marginTop: "2rem", padding: "1rem" }}>
        <Typography variant="h6" gutterBottom>
          Task List
        </Typography>
        <TableContainer>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell><strong>Task</strong></TableCell>
                <TableCell><strong>Subject</strong></TableCell>
                <TableCell><strong>Description</strong></TableCell>
                <TableCell><strong>Start Date</strong></TableCell>
                <TableCell><strong>Deadline</strong></TableCell>
                <TableCell><strong>Completed</strong></TableCell>
                <TableCell><strong>% Completed</strong></TableCell>
                <TableCell><strong>Actions</strong></TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {tasks.map((task) => (
                <TableRow key={task.taskid}>
                  <TableCell>{task.task}</TableCell>
                  <TableCell>{task.subject}</TableCell>
                  <TableCell>{task.desc}</TableCell>
                  <TableCell>{task.startdate}</TableCell>
                  <TableCell>{task.deadline}</TableCell>
                  <TableCell>{task.completed ? "Yes" : "No"}</TableCell>
                  <TableCell>{task.percentComp}%</TableCell>
                  <TableCell>
                    <Button variant="contained" color="primary" onClick={() => handleEdit(task)} style={{ marginRight: "8px" }}>
                      Edit
                    </Button>
                    <Button variant="contained" color="secondary" onClick={() => handleDelete(task.taskid)}>
                      Delete
                    </Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>
    </div>
  );
};

export default CreateTask;
