import React, { useState } from 'react';

function ChatInterface() {
  const [inputText, setInputText] = useState('');
  const [result, setResult] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleTextChange = (e) => {
    setInputText(e.target.value);
  };

  // Function extracted outside the loop to avoid ESLint warning
  const processChunk = (chunk, accumulatedResult, setResult) => {
    const lines = chunk.split('\n');
    lines.forEach((line) => {
      if (line.startsWith('data:')) {
        const data = line.replace('data: ', '').trim();

        if (data === '[DONE]') {
          setIsLoading(false);
          return;
        }

        try {
          const parsedData = JSON.parse(data);
          if (parsedData.response) {
            accumulatedResult += parsedData.response;
            setResult(accumulatedResult);
          }
        } catch (err) {
          console.error('Error parsing JSON chunk:', err);
        }
      }
    });
  };

  const handleSubmit = async () => {
    if (inputText.trim()) {
      setIsLoading(true);
      setResult('');

      try {
        const response = await fetch(`https://cuddly-adventure-g4xwpjpw4v7r25vv-5000.app.github.dev/record/chat`, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ content: inputText }),
        });

        if (!response.ok) throw new Error('Network response was not ok');

        const reader = response.body.getReader();
        const decoder = new TextDecoder();

        let done = false;
        let accumulatedResult = '';

        while (!done) {
          const { value, done: readerDone } = await reader.read();
          done = readerDone;

          if (value) {
            const chunk = decoder.decode(value, { stream: true });
            processChunk(chunk, accumulatedResult, setResult);
          }
        }

        setIsLoading(false);
      } catch (error) {
        console.error('Error during streaming:', error);
        setIsLoading(false);
        setResult('An error occurred. Please try again.');
      }
    }
  };

  return (
    <div>
      <h1>Chat with Gemma 2:2b</h1>

      <div>
        <textarea
          value={inputText}
          onChange={handleTextChange}
          placeholder="Type your message here..."
          rows="5"
          cols="50"
          disabled={isLoading}
        />
      </div>

      <button onClick={handleSubmit} disabled={isLoading}>
        {isLoading ? 'Waiting for response...' : 'Send'}
      </button>

      <div>
        <h2>Response:</h2>
        <pre style={{ whiteSpace: 'pre-wrap', wordWrap: 'break-word' }}>{result}</pre>
      </div>
    </div>
  );
}

export default ChatInterface;
