import { Grid } from "@mui/material";
import { useState, useEffect } from "react";
import PieChart from "./PieChart";
import Cards from "./DashboardCard";

const Dashboard = () => {
  const [pieData, setPieData] = useState([0, 0, 0, 0]);

  useEffect(() => {
    fetch("http://localhost:5000/api/tasks")
      .then((response) => response.json())
      .then((tasks) => {
        let completed = 0, inProgress = 0, due = 0, notStarted = 0;

        tasks.forEach((task) => {
          if (task.completed) completed++;
          else if (task.dueDate && new Date(task.dueDate) < new Date()) due++;
          else if (task.started) inProgress++;
          else notStarted++;
        });

        setPieData([completed, inProgress, due, notStarted]);
      })
      .catch((error) => console.error("Error fetching tasks:", error));
  }, []);

  return (
    <Grid container spacing={2}>
      <Grid item sm={3} xs={12}>
        <Cards color="#00E676" data={{ num: pieData[0], label: "Completed" }} />
      </Grid>
      <Grid item sm={3} xs={12}>
        <Cards color="#29B6F6" data={{ num: pieData[1], label: "In Progress" }} />
      </Grid>
      <Grid item sm={3} xs={12}>
        <Cards color="#F50057" data={{ num: pieData[2], label: "Due" }} />
      </Grid>
      <Grid item sm={3} xs={12}>
        <Cards color="#FBC02D" data={{ num: pieData[3], label: "Not Started" }} />
      </Grid>
      <Grid item xs={12}>
        <PieChart 
          data={[['Status', 'Count'], ['Completed', pieData[0]], ['In Progress', pieData[1]], ['Due', pieData[2]], ['Not Started', pieData[3]]]} 
          title="Task Distribution" 
        />
      </Grid>
    </Grid>
  );
};

export default Dashboard;
