import React, { useEffect, useState } from 'react';

const Profile = () => {
  const [profiles, setProfiles] = useState([]);
  const [selectedProfile, setSelectedProfile] = useState(null);

  // Fetch profiles from backend
  useEffect(() => {
    const fetchProfiles = async () => {
      try {
        const response = await fetch('/api/profiles'); // Updated endpoint for multiple profiles
        const data = await response.json();
        setProfiles(data);
        if (data.length > 0) {
          setSelectedProfile(data[0]); // Default to first profile
        }
      } catch (error) {
        console.error("Error fetching profiles:", error);
      }
    };

    fetchProfiles();
  }, []);

  // Handle profile selection
  const handleProfileSelect = (e) => {
    const selectedId = e.target.value;
    const profile = profiles.find((p) => p._id === selectedId);
    setSelectedProfile(profile);
  };

  if (!profiles.length) {
    return <div>Loading profiles...</div>;
  }

  return (
    <div className="profile-container">
      <h2>Student Details</h2>

      {/* Profile Selection Dropdown */}
      <select onChange={handleProfileSelect} value={selectedProfile?._id || ""}>
        {profiles.map((profile) => (
          <option key={profile._id} value={profile._id}>
            {profile.name}
          </option>
        ))}
      </select>

      {/* Profile Details Display */}
      {selectedProfile && (
        <div className="profile-details">
          <p><strong>Name:</strong> {selectedProfile.name}</p>
          <p><strong>Email:</strong> {selectedProfile.email}</p>
          <p><strong>Role:</strong> {selectedProfile.role}</p>
        </div>
      )}
    </div>
  );
};

export default Profile;
